import { initializeApp } from "firebase/app";
import {
  getAuth,
  signInWithRedirect,
  signInWithPopup,
  GoogleAuthProvider,
  createUserWithEmailAndPassword,
} from "firebase/auth";

import {
  getFirestore,
  doc, // retrieves documents from Firestore data base
  getDoc, // getting documents data
  setDoc, // setting document data
} from "firebase/firestore";

// Import the functions you need from the SDKs you need
//import { getAnalytics } from "firebase/analytics";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyDin_6rtTjSSFBuvnlcxuZsRy8VRkkUUwA",
  authDomain: "ecommerce-clothing-4d687.firebaseapp.com",
  projectId: "ecommerce-clothing-4d687",
  storageBucket: "ecommerce-clothing-4d687.appspot.com",
  messagingSenderId: "1092420712689",
  appId: "1:1092420712689:web:077a19b09202804bcd9150",
  measurementId: "G-DY4EMZ572Y",
};

// Initialize Firebase
const firebaseApp = initializeApp(firebaseConfig);
//const analytics = getAnalytics(app);

const googleProvider = new GoogleAuthProvider();

googleProvider.setCustomParameters({
  prompt: "select_account",
});

export const auth = getAuth();
export const signInWithGooglePopup = () =>
  signInWithPopup(auth, googleProvider);
export const signInWithGoogleRedirect = () =>
  signInWithRedirect(auth, googleProvider);

// Instantiate our fire store data base
export const db = getFirestore();

export const createUserDocumentFromAuth = async (userAuth, additionalInformation) => {

  if (!userAuth) return;

  const userDocRef = doc(db, "users", userAuth.uid);

  console.log(userDocRef);

  const userSnapshot = await getDoc(userDocRef);
  //console.log(userSnapshot);

  // checks if a user exists in the document within the db
  //console.log(userSnapshot.exists());

  // if user data does not exist
  // create and set the document with the data from the userAuth in my collection
  if (!userSnapshot.exists()) {
    const { displayName, email } = userAuth;
    const createdAt = new Date();

    try {
      await setDoc(userDocRef, {
        displayName,
        email,
        createdAt,
      });
    } catch (error) {
      console.log("Error creating the user", error.message);
    }
  }
  // if user data exists
  return userDocRef;

  // return user document reference
};


export const createAuthUserWithEmailAndPassword = async (email, password) => {
  if ((!email) || (!password)) return;

  return await createAuthUserWithEmailAndPassword(auth, email, password);
};
